const disableScroll = function() {
    let paddingOffset = window.innerWidth - document.body.offsetWidth + 'px'
    document.body.classList.toggle('scroll-lock')
    document.body.style.paddingRight = paddingOffset
}

const burger = document.querySelector('.menu-icon').addEventListener('click', function() {
    const menu = document.querySelector('.menu')
    this.classList.toggle('menu-icon__active')
    menu.classList.toggle('menu__active')
    disableScroll()

    let links = document.querySelectorAll('.menu__link').forEach(element => {
        element.onclick = () => {
            menu.classList.toggle('menu__active')
            this.classList.toggle('menu-icon__active')
            disableScroll()
        }
    })
})

const form = document.querySelector('.form__button').addEventListener('click', (e) => {
    e.preventDefault()
    createSuccessModal()
})


const createSuccessModal = (modal, bg) => {

    modal = document.createElement ('div')
    modal.className = 'success modal__active'
    modal.insertAdjacentHTML('afterbegin', `
            <h2 class="success__title">Form submitted successfully</h2>
            <p class="success__subtitle">We will call you back shortly</p>
            <button class="success__exit">close</button>
    `)
    disableScroll()

    bg = document.createElement ('div')
    bg.className = 'modal__bg'

    document.body.append(modal)
    document.body.append(bg)

    bg.addEventListener('click', () => {
        document.body.removeChild(modal)
        document.body.removeChild(bg)
        modal.classList.remove('modal__active')
    })
    const close = document.querySelector('.success__exit').onclick = () => {
        document.body.removeChild(modal)
        document.body.removeChild(bg)
        modal.classList.remove('modal__active')
        disableScroll()
    }

}



